INSERT INTO `quest_greeting_locale` (`ID`, `type`, `locale`, `Greeting`, `VerifiedBuild`) VALUES (823, 0, 'ruRU', 'Привет, $c.  Обычно я был бы на дежурстве, присматривая за жителями Штормграда, но многие стражи Штормграда сражаются в других землях.  И вот я здесь, исполняю обязанности заместителя и предлагаю награды, хотя предпочел бы быть в патруле...', 0);
